var data = {};

exports._setup = function(method, filename, params, callback)
{
	data[method] = {
		'filename': filename,
		'params': params,
		'callback': callback
	};

	this[method] = function(filename, params, callback) {
		expect(data[method].filename).to.equal(filename);
		expect(data[method].params).to.eql(params);
		data[method].callback();
	};
};
